mapper.insert:
insert into
	item_db(
		item_num
		,acc_id
		,item_option1
		,item_option2
		,item_title
		,item_contents
		,item_price
		,item_fileNum
	)values(
		item_db_seq.nextval
		,#{acc_id}
		,#{item_option1}
		,#{item_option2}
		,#{item_title}
		,#{item_contents}
		,#{item_price}
		,#{item_fileNum}
	)

		select item_db_seq.currval
		from dual
